$(document).on('click', '#search', function () {

    debugger;
    var valid = true;
    var errorMessages = "<ul>";
    var checkin = $("#checkin").val();
    var checkout = $("#checkout").val();

    if (checkin === "") {
        $("#checkin").addClass("error");
        errorMessages = errorMessages + "<li>Please enter your Check In Date</li>";
        valid = false;
    }
    if (checkout === "") {
        $("#checkout").addClass("error");
        errorMessages = errorMessages + "<li>Please enter your Check Out Date</li>";
        valid = false;
    }
    errorMessages = errorMessages + "</ul>";

    if (valid) {
        debugger;
        var bookData = {
            'NumberOfRomms': $("#NumberOfRooms").val(),
            'CheckInDate': $("#checkin").val(),
            'CheckOutDate': $("#checkout").val(),
            'NumberOfAdult': $("#Adults").val(),
            'NumberOfChild': $("#Childs").val()
        };
        var url = "/Reservation/home/GetBooks";
        $.ajax({
            type: 'POST',
            url: url,
            data: {
                'searchRequest': bookData
            },
            success(data) {
                debugger;
                if (data != null) {

                }
            },
            error(data) {
                debugger;
            }
        });
    }
    else {
        $('.errorDisplay').removeClass('d-none');
        $('.errorDisplay').html(errorMessages);
        return false;
    }

});